CREATE TABLE `trama_medios-entrega` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `denominacion` VARCHAR(50) NULL , `importe` FLOAT NULL , `activo` TINYINT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `trama_medios-pagos` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `denominacion` VARCHAR(50) NULL , `pasarelaPago` INT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `trama_cuotas-recargos` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `idMedioPago` INT NULL , `cuotas` INT NULL , `recargo` FLOAT NULL , `activo` TINYINT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
ALTER TABLE `trama_medios-pagos` ADD `activo` TINYINT NULL AFTER `pasarelaPago`;
ALTER TABLE `walger_pedidos` ADD `idMedioEnvio` INT UNSIGNED NULL AFTER `comentario`, ADD `idMedioPago` INT UNSIGNED NULL AFTER `idMedioEnvio`, ADD `idCuotaRecargo` INT UNSIGNED NULL AFTER `idMedioPago`, ADD `recargoEnvio` FLOAT NULL AFTER `idCuotaRecargo`, ADD `recargoPago` FLOAT NULL AFTER `recargoEnvio`, ADD `StatusCode` INT NULL AFTER `recargoPago`, ADD `StatusMessage` VARCHAR(100) NULL AFTER `StatusCode`, ADD `URL_Request` VARCHAR(150) NULL AFTER `StatusMessage`, ADD `RequestKey` VARCHAR(50) NULL AFTER `URL_Request`, ADD `PublicRequestKey` VARCHAR(50) NULL AFTER `RequestKey`, ADD `AnswerKey` VARCHAR(50) NULL AFTER `PublicRequestKey`;
ALTER TABLE `walger_pedidos` ADD `recargoEnvioIva` FLOAT NULL AFTER `idMedioPago`, ADD `recargoPagoIva` FLOAT NULL AFTER `recargoEnvioIva`;
CREATE TABLE `trama_pagos-entregas` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `idMedioEntrega` INT UNSIGNED NULL , `idMedioPago` INT UNSIGNED NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
ALTER TABLE `trama_medios-entrega` ADD `iva` FLOAT NULL AFTER `importe`;
CREATE TABLE `todopago_datos-adicionales` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `MERCHANT` VARCHAR(40) NULL COMMENT 'Nro. de Comercio (Merchant ID) provisto por TodoPago' , `OPERATIONID` INT NULL COMMENT 'Identificación de la transacción para el Comercio. Debe ser distinto para cada operación.' , `CURRENCYCODE` INT NULL COMMENT 'Tipo de moneda de la operación. Sólo válido pesos argentinos (32)' , `AMOUNT` FLOAT NULL COMMENT 'Importe en Pesos de la transacción.' , `EMAILCLIENTE` VARCHAR(80) NULL COMMENT 'El comercio deberá enviar a TodoPago el email del cliente. Esta dirección se utilizará para enviar el mail de confirmación de la compra al cliente' , `CSBTCITY` VARCHAR(50) NULL COMMENT 'Ciudad de facturación' , `CSBTCOUNTRY` VARCHAR(2) NULL COMMENT 'País de facturación' , `CSBTCUSTOMERID` VARCHAR(50) NULL COMMENT 'Identificador del usuario al que se le emite la factura. No puede contener un correo electrónico' , `CSBTIPADDRESS` VARCHAR(15) NULL COMMENT 'IP de la PC del comprador' , `CSBTEMAIL` VARCHAR(100) NULL COMMENT 'Mail del usuario al que se le emite la factura' , `CSBTFIRSTNAME` VARCHAR(60) NULL COMMENT 'Nombre del usuario al que se le emite la factura' , `CSBTLASTNAME` VARCHAR(60) NULL COMMENT 'Apellido del usuario al que se le emite la factura' , `CSBTPHONENUMBER` VARCHAR(15) NULL COMMENT 'Teléfono del usuario al que se le emite la factura. No utilizar guiones, puntos o espacios. Incluir código de país' , `CSBTPOSTALCODE` VARCHAR(10) NULL COMMENT 'Código Postal de la dirección de facturación' , `CSBTSTATE` VARCHAR(2) NULL COMMENT 'Provincia de la dirección de facturación' , `CSBTSTREET1` VARCHAR(60) NULL COMMENT 'Domicilio de facturación (Calle Numero interior Numero Exterior)' , `CSPTCURRENCY` VARCHAR(5) NULL COMMENT 'Moneda' , `CSPTGRANDTOTALAMOUNT` FLOAT NULL COMMENT '"999999.CC" Con decimales obligatorios, usando el puntos como separador de decimales. No se permiten comas, ni como separador de miles ni como separador de decimales.' , `CSSTCITY` VARCHAR(50) NULL COMMENT 'Ciudad de envío de la orden' , `CSSTCOUNTRY` VARCHAR(2) NULL COMMENT 'País de envío de la orden' , `CSSTEMAIL` VARCHAR(100) NULL COMMENT 'Correo electrónico del comprador' , `CSSTFIRSTNAME` VARCHAR(60) NULL COMMENT 'Nombre de la persona que recibe el producto' , `CSSTLASTNAME` VARCHAR(60) NULL COMMENT 'Apellido de la persona que recibe el producto' , `CSSTPHONENUMBER` VARCHAR(15) NULL COMMENT 'Número de teléfono del destinatario' , `CSSTPOSTALCODE` VARCHAR(10) NULL COMMENT 'Código postal del domicilio de envío' , `CSSTSTATE` VARCHAR(2) NULL COMMENT 'Provincia de envío' , `CSSTSTREET1` VARCHAR(60) NULL COMMENT 'Domicilio de envío' , `CSITPRODUCTCODE` VARCHAR(255) NULL COMMENT 'Categoria del producto' , `CSITPRODUCTDESCRIPTION` VARCHAR(255) NULL COMMENT 'Descripción del producto' , `CSITPRODUCTNAME` VARCHAR(255) NULL COMMENT 'Nombre del producto' , `CSITPRODUCTSKU` VARCHAR(255) NULL COMMENT 'Código identificador del producto' , `CSITTOTALAMOUNT` VARCHAR(255) NULL COMMENT 'CSITTOTALAMOUNT = CSITUNITPRICE * CSITQUANTITY "999999.CC" Es mandatorio informar los decimales, usando el punto como separador de decimales. No se permiten comas, ni como separador de miles ni como separador de decimales.' , `CSITQUANTITY` VARCHAR(255) NULL COMMENT 'Cantidad del producto' , `CSITUNITPRICE` VARCHAR(255) NULL COMMENT '"999999.CC" Es mandatorio informar los decimales, usando el punto como separador de decimales. No se permiten comas, ni como separador de miles ni como separador de decimales.' , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `todopago_provincias` ( `id` VARCHAR(1) NOT NULL , `denominacion` VARCHAR(30) NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
